demo.script:
function moveElsaUp() {
    jumpForward(2);
    show("elsa");
}
when("keyboard", "up", moveElsaUp);

function moveElsaDown() {
    jumpBackward(2);
    show("elsa");
}
when("keyboard", "down", moveElsaDown);

function moveElsaLeft() {
    turnLeft(90);
    jumpForward(2);
    turnRight(90);
    show("elsa");
}
when("keyboard", "left", moveElsaLeft);

function moveElsaRight() {
    turnRight(90);
    jumpForward(2);
    turnLeft(90);
    show("elsa");
}
when("keyboard", "right", moveElsaRight);

function moveElsaCenter() {
    resetToCenter();
    show("elsa");
}
when("keyboard", "c", moveElsaCenter);

function moveElsa(x, y) {
    jumpTo(x, y);
    show("elsa");
}
when("mouse", moveElsa);

show("elsa");

advanced-demo.script:
var elsaX = 0;
var elsaY = 0;

function moveElsa(dx, dy) {
    elsaX = elsaX + dx;
    elsaY = elsaY + dy;
    jumpTo(elsaX, elsaY);
    show("elsa");
}

function moveElsaUp() {
    moveElsa(0, 2);
}
when("keyboard", "up", moveElsaUp);

function moveElsaDown() {
    moveElsa(0, -2);
}
when("keyboard", "down", moveElsaDown);

function moveElsaLeft() {
    moveElsa(-2, 0);
}
when("keyboard", "left", moveElsaLeft);

function moveElsaRight() {
    moveElsa(2, 0);
}
when("keyboard", "right", moveElsaRight);

function moveElsaCenter() {
    elsaX = 0;
    elsaY = 0;
    moveElsa(0, 0);
}
when("keyboard", "c", moveElsaCenter);

function jumpElsa(x, y) {
    elsaX = x;
    elsaY = y;
    moveElsa(0, 0);
}
when("mouse", jumpElsa);

show("elsa");

Ticker.script:
var count = 0;

function ticker() {
    count = count + 1;
    if (count < 100) {
        jumpForward(1);
    } else {
        jumpBackward(1);
    }
    show("elsa");
    if (count > 200) {
        count = 0;
    }
}
when("tick", ticker);

fast wheel.script:
function dessin(color) {
    penColor(color);
    for (var i = 1; i <= 12; i++) {
        for (var j = 1; j <= 2; j++) {
            moveForward(100);
            turnRight(60);
            moveForward(100);
            turnRight(120);
        }
        turnLeft(30);
    }
}

function ticker() {
    dessin("yellow");
    turnRight(200);
    dessin("lightblue");
}
when("tick", ticker);
